example.code:
FileStream fileStream = new FileStream("Page1.png", FileMode.CreateNew, FileAccess.ReadWrite);
//Exports page as image.
await sfDocumentEditorContainer.DocumentEditor.ExportAsImageAsync(fileStream, 1, ImageFormat.Png);
fileStream.Dispose();

// Gets the selected content of the document as SFDT and paste the same in the document editor.
SfDocumentEditorContainer container; 
await string sfdt = container.DocumentEditor.Selection.GetSfdtAsync();
await container.DocumentEditor.Editor.PasteAsync(sfdt);

FileStream fileStream = new FileStream("Page1.png", FileMode.CreateNew, FileAccess.ReadWrite);
//Exports page as image.
await sfDocumentEditorContainer.DocumentEditor.ExportAsImageAsync(fileStream, 1, ImageFormat.Png);
fileStream.Dispose();
